function isPrime(number) {
    if (number === 1 || number === 2) {
        return false;
    }

    for (let i = 3; i <= Math.floor(Math.sqrt(number)); i += 2) {
        if (number % i === 0) {
            return false;
        }
    }
    return true;
}
