import math
from typing import Dict, List


class Solution:
    def can_form_array(self, arr: List[int]) -> bool:
        """
        输入: [1,2,3]
        输出: True
        """
        if len(arr) < 2:
            return False
        nums = set()
        for i in range(len(arr)):
            if i > 0 and arr[i - 1] == arr[i]:
                continue
            if arr[i] not in nums:
                nums.add(arr[i])
        if nums == set([0]):
            return True
        return False


if __name__ == '__main__':
    s = Solution()
    print(s.can_form_array([1, 2, 3]))

