def median(a):
    if len(a)%2 != 0:
        # If one array has even length, we must return its mid element.
        return a[len(a)/2]

    mid = int((len(a)/2))
    if len(a)<mid:
        # The shorter array contains less number of elements than the other array.
        return max(a)
    elif len(a)>mid:
        # The longer array contains more number of elements than the other array.
        return min(a)

    # Both arrays contain same number of elements, we must return the middle element.
